// Minimal local jQuery fallback stub for development only.
// This is intentionally tiny â€” it provides a very small subset of jQuery used
// in the project's templates if the CDN is unavailable. Prefer the CDN in prod.
(function(window){
  if (window.jQuery) return;
  function tiny$(sel){
    var nodes = document.querySelectorAll(sel);
    // Add a couple helper methods to mimic a tiny jQuery-like API
    nodes.each = function(cb){ Array.prototype.forEach.call(nodes, cb); };
    return nodes;
  }
  tiny$.ajax = function(opts){
    // Very small wrapper around fetch for basic GET/POST JSON requests
    var init = { method: opts.type || opts.method || 'GET', headers: opts.headers || {} };
    if (opts.data) init.body = opts.data;
    return fetch(opts.url, init).then(function(resp){
      var ct = resp.headers.get('content-type') || '';
      if (ct.indexOf('application/json') !== -1) return resp.json();
      return resp.text();
    });
  };
  window.jQuery = window.$ = tiny$;
})(window);

