/* Minimal local fallback for jQuery used only when CDN is unavailable.
   This file intentionally provides a tiny subset: a no-op jQuery-compatible
   wrapper for basic $ usage so pages that rely on simple selectors do not fail
   in environments without network access.

   If you prefer the real jQuery, replace this file with the official
   jquery-3.7.1.min.js distribution (download from https://jquery.com/).
*/
(function (global) {
  // Minimal shim: implement a very small subset of jQuery used by this project.
  function $(selector) {
    // If a function is provided, call it on DOMContentLoaded
    if (typeof selector === 'function') {
      if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', selector);
      } else {
        selector();
      }
      return;
    }
    // For selectors, use querySelectorAll and return an array-like object
    var nodes = document.querySelectorAll(selector || null);
    nodes.each = function (fn) {
      Array.prototype.forEach.call(nodes, fn);
    };
    return nodes;
  }
  // Expose minimal interface
  $.ajax = function () { throw new Error('$.ajax not implemented in local fallback. Include full jQuery if needed.'); };
  // Attach to window
  global.$ = global.jQuery = $;
})(window);

